package Simulation;

public class Item {

	String name;
	int weight;

	public Item(String name, int weight) {
		super();
		this.name = name;
		this.weight = weight;
	}

	public Item() {
		// TODO Auto-generated constructor stub
	}

	public String getName() {
		return name;
	}

	public int getWeight() {
		return weight;
	}
}
